import React, { useState } from 'react';
import { LocalizationProvider, DatePicker, TimePicker } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import dayjs from 'dayjs';
import 'dayjs/locale/pt-br';

function Agenda({ aoEnviar }) {
  const [data, setData] = useState(dayjs().locale('pt-br'));
  const [hora, setHora] = useState(dayjs().locale('pt-br'));

  const handleDataChange = (date) => {
    setData(date);
  };

  const handleHoraChange = (time) => {
    setHora(time);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    aoEnviar({ data, hora });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Data do Agendamento:</label>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DatePicker
              value={data}
              onChange={handleDataChange}
              renderInput={(props) => <input {...props} readOnly />}
            />
          </LocalizationProvider>
        </div>
        <div>
          <label>Horas do Agendamento:</label>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <TimePicker
              value={hora}
              onChange={handleHoraChange}
              ampm={false} // Defina para true se desejar usar o formato 12 horas
              renderInput={(props) => <input {...props} readOnly />}
            />
          </LocalizationProvider>
        </div>
        <button type="submit">Finalizar Cadastro</button>
      </form>
    </div>
  );
}

export default Agenda;
