import React, { useState } from "react";
import { Button, Grid, } from "@material-ui/core";
// import ValidacoesCadastro from "../../contexts/ValidacoesCadastro";
// import useErros from "../../models/hooks/useErros";
import produto from './R.jpeg'
import styled from "@emotion/styled";

function Corte({ aoEnviar }) {
  const [corte, setCorte] = useState('');
  const [barba, setBarba] = useState('');
  const [bigode, setBigode] = useState('');


  // const validacoes = useContext(ValidacoesCadastro)
  // const [erros, validarCampos, possoEnviar] = useErros(validacoes);
  
  const produtoImg = styled.img`

    width: 100%;
    height:40vh;
    flex-wrap: nowrap;
    @media (min-width: 768px) {

    }
`;

  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        // if (possoEnviar()) {
          aoEnviar({ corte, barba, bigode });
        // }
      }}
    >
    
    <Grid container spacing={2}>
      <Grid
          value={corte}
          onChange={(event) => {
            setCorte(event);
          }} 
        item xs={4}>
         <produtoImg src={produto} alt={produto} />
      </Grid>
      <Grid
          value={barba}
          onBarba={(event) => {
            setBarba(event);
          }} 
        item xs={4}>
        <produtoImg src={produto} alt={produto} />
      </Grid>
      <Grid
          value={bigode}
          onChange={(event) => {
            setBigode(event);
          }} 
          item xs={4}>
        <produtoImg src={produto} alt={produto} />
      </Grid>
    </Grid>

      <Button type="submit" variant="contained" color="primary">
        Próximo
      </Button>
    </form>
  );
}

export default Corte;
