import React, { useState, useContext } from "react";
import { Button, ButtonGroup, Card, CardActionArea, CardMedia, Typography } from "@material-ui/core";
import ValidacoesCadastro from "../../contexts/ValidacoesCadastro";
import useErros from "../../models/hooks/useErros";

function Corte({ aoEnviar }) {
  const [email, setEmail] = useState("");
  const [options, setOptions] = useState("");


  // const validacoes = useContext(ValidacoesCadastro)
  // const [erros, validarCampos, possoEnviar] = useErros(validacoes);
  
  const [selectedOption, setSelectedOption] = useState(null);

 options = [
    // { label: "Opção 1", image: "/path/to/image1.jpg" },
    // { label: "Opção 2", image: "/path/to/image2.jpg" },
    // { label: "Opção 3", image: "/path/to/image3.jpg" },

    { label: "Opção 1" },
    { label: "Opção 2" },
    { label: "Opção 3" },
  ];
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        // if (possoEnviar()) {
          aoEnviar({ options });
        // }
      }}
    >
      <CardActionArea>
        {options.map((option, index) => (
          <ButtonGroup 
          key={index} variant="text" aria-label="outlined button group">
            <Button
              onClick={() => setSelectedOption(index)}
              color={selectedOption === index ? "primary" : "default"}
            >
              <CardMedia
                component="img"
                image={option.image}
                alt={option.label}
                height="100"
              />
              <Typography variant="body2" component="p">
                {option.label}
              </Typography>
            </Button>
          </ButtonGroup>
        ))}
      </CardActionArea>
      <Button type="submit" variant="contained" color="primary">
        Próximo
      </Button>
    </form>
  );
}

export default Corte;
