import React, { useEffect, useState } from "react";
import DadosPessoas from "./DadosPessoas";
import DadosUsuarios from "./DadosUsuarios";
import DadosEntrega from "./DadosEntrega";

function FormularioCadastro({aoEnviar, validarCPF}) {
const [etapaAtual, setEtapaAtual] = useState(0)
const [dadosColetados, setDados] = useState({})

useEffect(()=> {
  if (etapaAtual === formularios.length){
    aoEnviar(dadosColetados)
  }
})

const formularios = [ 
<DadosUsuarios aoEnviar={coletarDados} />,
<DadosPessoas aoEnviar={coletarDados}  validarCPF={validarCPF}/>,
<DadosEntrega aoEnviar={coletarDados} />,

]

function coletarDados (dados){
  setDados({...dadosColetados, ...dados})
  proximo()
}

function proximo () {
  setEtapaAtual(etapaAtual + 1)
}
  // function formularioAtual (etapa){
  //   switch (etapa){ 
  //     case 0:
  //       return <DadosUsuarios aoEnviar={proximo}/>
  //     case 1:
  //       return <DadosPessoas aoEnviar={proximo}/>
  //     case 2:
  //       return <DadosEntrega aoEnviar={aoEnviar}/>
  //     default:
  //       return <Typography>Erro ao selecionar formulário</Typography>
  //   }
  // }

    return <> {formularios[etapaAtual]} </>
}

export default FormularioCadastro;
