import React, { useState, useEffect } from 'react';

function Agenda({ aoEnviar }) {
  const [data, setData] = useState('');
  const [hora, setHora] = useState('');

  useEffect(() => {
    // Função para obter a data e hora atual no fuso horário de São Paulo
    const obterDataHoraSaoPaulo = async () => {
      try {
        const response = await fetch('http://worldtimeapi.org/api/timezone/America/Sao_Paulo');
        const data = await response.json();

        if (data && data.datetime) {
          const { datetime } = data;
          const dataHoraSaoPaulo = new Date(datetime);
          const dataAtual = dataHoraSaoPaulo.toISOString().slice(0, 10);
          const horaAtual = dataHoraSaoPaulo.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' });

          setData(dataAtual);
          setHora(horaAtual);
        }
      } catch (error) {
        console.error('Erro ao obter a data e hora de São Paulo:', error);
      }
    };

    obterDataHoraSaoPaulo();
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    aoEnviar({ data, hora });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Data do Agendamento:</label>
          <input type="date" value={data} readOnly />
        </div>
        <div>
          <label>Horas do Agendamento:</label>
          <input type="time" value={hora} readOnly />
        </div>
        <button type="submit">Finalizar Cadastro</button>
      </form>
    </div>
  );
}

export default Agenda;
