import React, { useState, useContext } from "react";
import { Button, Checkbox, FormControlLabel, FormGroup, Typography } from "@material-ui/core";
// import ValidacoesCadastro from "../../contexts/ValidacoesCadastro";
// import useErros from "../../models/hooks/useErros";

function Corte({ aoEnviar }) {
  const [selectedOption, setSelectedOption] = useState('');


  // const validacoes = useContext(ValidacoesCadastro)
  // const [erros, validarCampos, possoEnviar] = useErros(validacoes);
  

 const options = [
    { label: "Opção 1"},
    { label: "Opção 2"},
    { label: "Opção 3"},


  ];
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        // if (possoEnviar()) {
          aoEnviar({ selectedOption });
        // }
      }}
    >
      <FormGroup>
        {options.map((option, index) => (
          <FormControlLabel key={index}>
            <Checkbox
              checked={selectedOption === option.label} // Verifica se a opção está selecionada
              onChange={() => setSelectedOption(option.label)} // Atualiza a opção selecionada com o valor da opção
              color={selectedOption === option.label ? "primary" : "default"} // Mude a cor com base na seleção
            />
            <Typography variant="body2" component="p">
              {option.label}
            </Typography>
          </FormControlLabel>
        ))}
      </FormGroup>
      <Button type="submit" variant="contained" color="primary">
        Próximo
      </Button>
    </form>
  );
}

export default Corte;
