import React, { useContext, useState } from "react";
import { TextField, Button } from "@material-ui/core";
// import ValidacoesCadastro from "../../contexts/ValidacoesCadastro"
// import useErros from "../../models/hooks/useErros";


function Agenda({ aoEnviar }) {
  // const [data, setData] = useState("");
  const [hora, setHora] = useState("");
  const [data, setData] = React.useState(dayjs('2023-10-25'));

  // const validacoes = useContext(ValidacoesCadastro)
  // const [erros, validarCampos, possoEnviar] = useErros(validacoes);

  // const [numero, setNumero] = useState("");
  // const [estado, setEstado] = useState("");
  // const [cidade, setCidade] = useState("");

  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        // if (possoEnviar()) {
          aoEnviar({ data, hora});
        // }
      }}
    >

      {/* <TextField
        value={data}
        onChange={(event) => {
          setData(event.target.value);
        }}
        // onBlur={validarCampos}
        // error={!erros.data.valido}
        // helperText={erros.data.texto}
        id="data"
        name="data"
        label=""
        type="date"
        variant="outlined"
        margin="normal"
        fullWidth
      /> */}
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <DateCalendar
        value={value}
        onChange={(newValue) => setValue(newValue)}
        showDaysOutsideCurrentMonth
        displayWeekNumber
        slots={{ day: Day }}
        slotProps={{
          day: (ownerState) => ({
            selectedDay: value,
            hoveredDay,
            onPointerEnter: () => setHoveredDay(ownerState.day),
            onPointerLeave: () => setHoveredDay(null),
          }),
        }}
      />
    </LocalizationProvider>
     <TextField
        value={hora}
        onChange={(event) => {
          setHora(event.target.value);
        }}
        // onBlur={validarCampos}
        // error={!erros.hora.valido}
        // helperText={erros.hora.texto}
        id="hora"
        name="hora"
        label=""
        type="time"
        variant="outlined"
        margin="normal"
        fullWidth
      />

      <Button type="submit" variant="contained" color="primary" fullWidth>
        Finalizar Cadastro
      </Button>
    </form>
  );
}

export default Agenda;
