import React, { useEffect, useState } from "react";
import DadosPessoas from "./DadosPessoas";
import Corte from "./Corte";
import Agenda from "./Agenda";
import { Step, StepLabel, Stepper, Typography } from "@material-ui/core";
import axios from "axios";

function FormularioCadastro({aoEnviar, validacoes}) {
const [etapaAtual, setEtapaAtual] = useState(0)
const [dadosColetados, setDados] = useState({})

useEffect(()=> {
  if (etapaAtual === formularios.length-1){
    aoEnviar(dadosColetados)
  }
})



const formularios = [ 
  <DadosPessoas aoEnviar={coletarDados} />,
  <Corte aoEnviar={coletarDados}/>,
  <Agenda aoEnviar={coletarDados}/>,
  <Typography variant="h5">Obrigado pela preferência!</Typography>

]

const urlAgenda = 'http://http://localhost:3000/'

        
  if (validateForm()) {

    function coletarDados (dados){
      setDados({...dadosColetados, ...dados})
      proximo()
      axios.post(urlAgenda, coletarDados)

      .then(() =>{
          alert( ' adicionado com sucesso na categoria ')
          onClose()
          })
      .catch(error => console.log(error))
    }
    }
  

function proximo () {
  setEtapaAtual(etapaAtual + 1)
}
  // function formularioAtual (etapa){
  //   switch (etapa){ 
  //     case 0:
  //       return <Corte aoEnviar={proximo}/>
  //     case 1:
  //       return <DadosPessoas aoEnviar={proximo}/>
  //     case 2:
  //       return <Agenda aoEnviar={aoEnviar}/>
  //     default:
  //       return <Typography>Erro ao selecionar formulário</Typography>
  //   }
  // }

    return <>
      <Stepper activeStep={etapaAtual}>
        <Step><StepLabel>Pessoal</StepLabel></Step>
        <Step><StepLabel>Corte</StepLabel></Step>
        <Step><StepLabel>Agenda</StepLabel></Step>
        <Step><StepLabel>Finalizacao</StepLabel></Step>
      </Stepper>
    {formularios[etapaAtual]} </>
}

export default FormularioCadastro;
