import React, { useState, useContext } from "react";
import { Button, ButtonGroup, Card, CardActionArea, CardMedia, Grid, Typography } from "@material-ui/core";
import ValidacoesCadastro from "../../contexts/ValidacoesCadastro";
import useErros from "../../models/hooks/useErros";

function Corte({ aoEnviar }) {
  const [selectedOption, setSelectedOption] = useState('');


  // const validacoes = useContext(ValidacoesCadastro)
  // const [erros, validarCampos, possoEnviar] = useErros(validacoes);
  

 const options = [
    { label: "Opção 1", image: "../../../public/img/R.jpeg" },
    { label: "Opção 2", image: "../../../public/img/R.jpeg" },
    { label: "Opção 3", image: "../../../public/img/R.jpeg" },


  ];
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        // if (possoEnviar()) {
          aoEnviar({ selectedOption });
        // }
      }}
    >
      <CardActionArea>
        {options.map((option, index) => (
          <Grid 
          key={index} variant="text" aria-label="outlined button group">
            <Grid
              onClick={() => setSelectedOption(index)}
              color={selectedOption === index ? "primary" : "default"}
            >
              <CardMedia
                component="img"
                image={option.image}
                alt={option.label}
                height="100"
              />
              <Typography variant="body2" component="p">
                {option.label}
              </Typography>
            </Grid>
          </Grid>
        ))}
      </CardActionArea>
      <Button type="submit" variant="contained" color="primary">
        Próximo
      </Button>
    </form>
  );
}

export default Corte;
