import React, { useState } from "react";
import { Box, Button, Container, Grid, TextField } from "@material-ui/core";
// import ValidacoesCadastro from "../../contexts/ValidacoesCadastro";
// import useErros from "../../models/hooks/useErros";
import produto from './R.jpeg'

function Corte({ aoEnviar }) {
  const [corte, setCorte] = useState('');
  const [barba, setBarba] = useState('');
  const [bigode, setBigode] = useState('');


// const validacoes = useContext(ValidacoesCadastro)
// const [erros, validarCampos, possoEnviar] = useErros(validacoes);



  return (
    <form sx={{ width:'100%'}}
      onSubmit={(event) => {
        event.preventDefault();
        // if (possoEnviar()) {
          aoEnviar({ corte });
        // }
      }}
    >
      <h2 style={{margin:'1rem', color:'#3f51b5', fontSize:'2rem'}}>Corte</h2>
      <Box  sx={{ display: 'flex', flexDirection:'row', alignItems:'center', justifyContent:'between',gap: '1rem'} }>
        <Box width={150}>
          <img
            width={150}
            src={produto}       
            value={corte}
            onChange={(event) => {
              setCorte(event);
            }} />
        </Box>
        <Box width={150}>
          <img
            width={150}
            src={produto}       
            value={barba}
            onChange={(event) => {
              setBarba(event);
            }} />
        </Box>
        <Box width={150}>
          <img
            width={150}
            src={produto}       
            value={bigode}
            onChange={(event) => {
              setBigode(event);
            }} />
        </Box>
      </Box>
      <Box sx={{display:'flex', justifyContent:'space-between', margin:'1rem 0', width:'100%'}}>
        <Button type="submit" variant="contained" color="primary">
          Anterior
        </Button>
        <Button type="subzmit" variant="contained" color="primary">
          Próximo
        </Button>
      </Box>

    </form>
  );
}

export default Corte;
