  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        // if (possoEnviar()) {
          aoEnviar({ corte });
        // }
      }}
    >
      <img
        width={200}
        src={produto}       
         value={corte}
         onChange={(event) => {
           setCorte(event);
         }} />
      <Button type="submit" variant="contained" color="primary">
        Próximo
      </Button>
    </form>
  );
}

export default Corte;
